INSERT INTO `as_user` VALUES ('15', '丹溪路', '丹丹', '1', 'ceb06295d73ee99df5b19f3d98b1ba84', '1', '1', '1', '1', '2', '210000', '210100', '210102', 'danxilu@789.cot', '18975689457', '/file/upload/images/avatar/2019/4/avatar_20190406223319898230131.jpg', '我来自郑州，我是一名信息工程大学印刷工程专业应届本科即将毕业的学员。\r\n我的性格偏于内向，为人坦率、热情、讲求原则；处事乐观、专心、细致、头脑清醒；富有责任心、乐于助人。\r\n我还是一个正直忠诚、勤奋求实的人，会不断追求人格的自我完善；明显的特点是乐观自信、温和开朗、稳重宽厚，因此，我人际关系和谐，适应环境能力较强。爱好电脑、科普知识，喜欢钻研，正因为这些增强了我的实干精神，习惯事情或问题及早解决。\r\n最大的满足感在于该做的事顺利完成，以便有精力去关注其他，也就是追求感觉上的自我良好或优越感。最大的缺点是对没有秩序感的人可能缺乏足够的耐心，也可能因为心太细，会让别人误解我的一番好意。', '20', '202L', '0', '0', '2019-04-06 22:33:19', '2019-04-06 22:33:19', '2019-04-06 22:33:19', null);
